import React from 'react'
import { connect } from 'react-redux'

const hats = ({ hats }) => (
    <div>
        { hats.map(hat => 
            <li key={hat.id}>{hat}</li>
        )}
    </div>
);

const mapStateToProps = state => ({
    hats: state.allPosts
});

export default connect(mapStateToProps)(hats);
