import { all } from 'redux-saga/effects'
import { CartActionTypes } from '../cart/cart.types'

export function* addItem (){
    yield takeEvery(CartActionTypes.ADD_ITEM)
}

export function* addItemSaga () {
    yield all([
        addItem()
    ])
}
