import { all, takeLatest, call } from 'redux-saga/effects'
import CartActionTypes from '../cart/cart.types'

export function* fetchAllShopsAsync() {
    try {
        const allShops = yield call(fetchAllShops);
        yield call(fetchAllShopsSuccess, allShops);
    } catch(error) {
        yield call(fetchAllShopAsyncFailure);
    }
}

export function* fetchAllShopsSaga() {
    yield takeLatest(CartActionTypes.ADD_ITEM, fetchAllShopsAsync);
}

export function* shopSaga() {
    yield all([
        fetchAllShopsSaga()
    ]);
}

async function fetchAllShops() {
    const response = await fetch('https://jsonplaceholder.typicode.com/posts/1').then(res => res.json());
    return response;
}
