import React from 'react'
import { connect } from 'react-redux'

import { clearItemFromCart } from '../../redux/cart/cart.actions'

import '../checkout-item/checkout-item.styles.scss'

const CheckoutItem = ({ cartItem: { name, imageUrl, price, quantity }, clearItem }) => (
    <div className='checkout-item'>
        <div className='image-container'>
            <img src={imageUrl} alt='item'/>
        </div>
        <span className='name'>{name}</span>
        <span className='quantity'>{quantity}</span>
        <span className='price'>{price}</span>
        <div className='remove-button' onClick={clearItem}>
            &#10005;
        </div>
    </div>
);

const mapDistpatchToProps = dispatch => ({
    clearItem: item => dispatch(clearItemFromCart(item))
})


export default connect(null, mapDistpatchToProps)(CheckoutItem);
