import { all, takeLatest, call, put } from 'redux-saga/effects'
import HatsActionTypes from '../hats/hats-types';
import { fetchAllHatsSuccess, fetchAllHatsFailure } from '../hats/hats.actions';

export function* fetchAllPostsAsync() {
    const allPosts = yield call(fetchAllPosts);
    if (allPosts) {
        yield put(fetchAllHatsSuccess(allPosts));
    } else {
        yield put(fetchAllHatsFailure());
    }
}

export function* fetchAllPostsSaga() {
    yield takeLatest(HatsActionTypes.FETCH_ALL_HATS, fetchAllPostsAsync);
}

export function* postsSaga() {
    yield all([
        fetchAllPostsSaga()
    ]);
}

async function fetchAllPosts() {
    const response = await fetch('http://my-json-server.typicode.com/Bakra/react-app/items').then(res => res.json()).catch(error => {
        return null;
    });
    return response;
}
