import { all, takeLatest } from 'redux-saga/effects'

export function* fetchAllShopsSaga() {
    yield takeLatest(CartActionTypes.ADD_ITEM)
}

export function* shopSaga() {
    all([
        fetchAllShopsSaga()
    ]);
}
